import React from 'react';
import { CircularProgressbar } from 'react-circular-progressbar';
import { MdCheckCircle, MdError, MdLink } from 'react-icons/md';

import { Container, FileInfo, Preview } from './styles';

const FileList = ({ files }) => (
    <Container>
        { files.map(uploadedFile => (
            <li>
                <FileInfo>
                    <Preview src={uploadedFile.preview} />

                    <div>
                        <strong>{uploadedFile.name}</strong>
                        <span>{uploadedFile.readableSize} <button onClick={() => {}}>Excluir</button></span>
                    </div>
                </FileInfo>

                <div>
                    {!uploadedFile.upload && !uploadedFile.error && (
                        <CircularProgressbar
                            styles={{
                                root: { width: 24 },
                                path: { stroke: '#7159c1' }
                            }}
                            strokeWidth={10}
                            value={uploadedFile.progress}
                        />
                    )}

                    {uploadedFile.url && (
                        <a 
                            href="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcThtHZ961y6yHniATMBg9RgzjTVTwHN4rWfAUkmndu_1C984aQQPg&s"
                            target="_blank"
                            rel="noopener noreferrer"
                        >
                            <MdLink style={{ marginRight: 8 }} size={24} color="#222" />
                        </a>
                    )}

                    {uploadedFile.uploaded && <MdCheckCircle size={24} color="#78e5d5" />}
                    {uploadedFile.error && <MdError size={24} color="#e57878" />}
                </div>
            </li>
        )) }
    </Container>
);

export default FileList;
